/**
 * 异常处理程序
 */
const errorHandler = (error: ResponseError) => {
  const { response } = error;
  if (response && response.status) {
    // const errorText = codeMessage[response.status] || response.statusText;
    // const { status, url } = response;
    // notification.error({
    //   message: `请求错误 ${status}: ${url}`,
    //   description: errorText,
    // });
  }

  if (!response) {
    // notification.error({
    //   description: '您的网络发生异常，无法连接服务器',
    //   message: '网络异常',
    // });
  }
  throw error;
};

/**
 * 配置request请求时的默认参数
 */
const request = extend({
  // 默认错误处理
  errorHandler,
  // 默认请求是否带上cookie
  credentials: 'include',
  paramsSerializer: (params: any) => stringify(params, { arrayFormat: 'comma' }),
  prefix: api,
});

// 登录验证
request.interceptors.response.use(async (response) => {
  const { status } = response;
  if (status === 401) {
    localStorage.setItem(`_401`, `${moment().format('YYYY-MM-DD HH:mm:ss')}_${window.location.href}`);
    // 无权限查看，跳到登录页
    window.location.href = `${URL}/login/#/?callback=${encodeURIComponent(window.location.href)}`;
    // 返回一个永远不会resolve的promise
    return NEVER_RESOLVE as Promise<Response>;
  }
  return response;
});


// magic
request.interceptors.request.use(
  (_, options) => {
    const timestamp = Date.now();
    const salt = ~~(Math.random() * 10 ** 9);
    const number = ~~(Math.random() * 9 + 1);
    const sign = Array(5)
      .fill(0)
      .map(() => String.fromCharCode(65 + ~~(Math.random() * 26)))
      .join('');
    const ts = (timestamp - salt) * number;
    const rt = md5(`${timestamp}_[@]beinifaxianle${sign}`);
    const token = localStorage.getItem('JSESSIONID');
    if (token) {
      Cookies.set('JSESSIONID', token);
    }
    const headers = {
      ts: ts.toString(),
      salt: salt.toString(),
      number: number.toString(),
      sign,
      rt,
      token,
    };
    Object.assign(headers, options.headers);
    return {
      options: {
        ...options,
        headers,
      },
    };
  },
  { global: false },
);

const mock = extend({
  // 默认错误处理
  errorHandler,
  // 默认请求是否带上cookie
  credentials: 'include',
  prefix: MOCK_API_URL,
  paramsSerializer: (params) => stringify(params, { arrayFormat: 'comma' }),
});

export { request, mock };

export default request;
